function [Derivatives] = parameter_conf(Configuration, Footage)%% Creation of the derivatives vector switch Configuration          case 1      %---------------------------------------------------------------------         % Standard case, creates the row 0 / 0.5 / 1 / 1.5 etc     %---------------------------------------------------------------------     for i = 1:Footage         Derivatives(i) = (i - 1) * 0.5;     end              case 2      %---------------------------------------------------------------------         % Creates the row 0 / 1 / 2 / 3 / 4 etc     %---------------------------------------------------------------------              for i = 1:Footage         Derivatives(i) = i - 1;     end               case 3     %---------------------------------------------------------------------         % Creates the symmetric row -1.5/ -1 / -0.5 / 0 / 0.5 / 1 / 1.5 etc     %---------------------------------------------------------------------         for i = 1:Footage         Derivatives(i) = double((i - int32(Footage)/2 )) * 0.5;     end            case 4     %---------------------------------------------------------------------         % Creates the assymmetric row -1 / -0.5 / 0 / 0.5 / 1 / 1.5 / 2 etc     %---------------------------------------------------------------------         for i = 1:Footage         Derivatives(i) = double((i - int32(Footage)/3 )) * 0.5;     end               end